package com.zhangsheng.shunxin.weather.widgetimport android.content.Contextimport android.util.AttributeSetimport android.widget.GridView/** * Created by Quan on 2018/11/8 0008 20:27 * E-Mail Addressï¼š672114236@qq.com */class ScrollGridView : GridView {    constructor(context: Context, attrs: AttributeSet) : super(context, attrs)    constructor(context: Context) : super(context)    constructor(context: Context, attrs: AttributeSet, defStyle: Int) : super(context, attrs, defStyle)    public override fun onMeasure(widthMeasureSpec: Int, heightMeasureSpec: Int) {        var expandSpec =MeasureSpec.makeMeasureSpec(Integer.MAX_VALUE shr 2, MeasureSpec.AT_MOST)        super.onMeasure(widthMeasureSpec, expandSpec)    }}